// n8n Code node (JavaScript) - Run Once for All Items

function proximoDiaUtil(dateObj) {
  // dateObj √© um Date (com hora ignorada)
  // 0=dom, 6=sab
  while (dateObj.getDay() === 0 || dateObj.getDay() === 6) {
    dateObj.setDate(dateObj.getDate() + 1);
  }
  return dateObj;
}

function calcularPrazo(now) {
  const due = new Date(now);

  // antes de 12:00 -> hoje 18:00
  if (now.getHours() < 12) {
    due.setHours(18, 0, 0, 0);
    return due.toISOString();
  }

  // ap√≥s 12:00 -> pr√≥ximo dia √∫til 12:00
  const next = new Date(now);
  next.setDate(next.getDate() + 1);
  next.setHours(0, 0, 0, 0);

  proximoDiaUtil(next);
  next.setHours(12, 0, 0, 0);

  return next.toISOString();
}

function limparTexto(rawText) {
  // Remove ```json, ``` e tamb√©m um "json" solto no in√≠cio
  let cleaned = String(rawText);

  cleaned = cleaned.replace(/```(?:json)?/gi, "").replace(/```/g, "");
  cleaned = cleaned.trim();
  cleaned = cleaned.replace(/^\s*json\s*/i, "").trim();

  return cleaned;
}

const out = [];

for (const item of $input.all()) {
  const rawText = item.json?.output ?? "";

  if (!rawText) {
    out.push({
      json: { ok: false, error: "Campo 'output' vazio", raw: item.json },
    });
    continue;
  }

  const cleaned = limparTexto(rawText);

  try {
    const parsed = JSON.parse(cleaned);

    // S√≥ calcula prazo quando pode_criar = true e prazo vazio
    if (Boolean(parsed?.pode_criar)) {
      const prazo = parsed?.prazo;
      const prazoVazio =
        prazo === null ||
        prazo === undefined ||
        (typeof prazo === "string" && prazo.trim() === "");

      if (prazoVazio) {
        parsed.prazo = calcularPrazo(new Date());
      }
    }

    out.push({
      json: {
        ok: true,
        pode_criar: Boolean(parsed?.pode_criar ?? false),
        dados: parsed,
      },
    });
  } catch (e) {
    out.push({
      json: { ok: false, error: String(e?.message ?? e), raw_text: rawText },
    });
  }
}

return out;
