import json
import re
from datetime import datetime, timedelta

# Se quiser for√ßar fuso, use zoneinfo (Python 3.9+):
# from zoneinfo import ZoneInfo
# TZ = ZoneInfo("America/Fortaleza")

def proximo_dia_util(data):
    # 0=segunda ... 4=sexta, 5=sab, 6=dom
    while data.weekday() >= 5:
        data += timedelta(days=1)
    return data

def calcular_prazo(now: datetime) -> str:
    # regra: antes de 12:00 -> hoje 18:00 | ap√≥s 12:00 -> pr√≥ximo dia √∫til 12:00
    if now.hour < 12:
        due = now.replace(hour=18, minute=0, second=0, microsecond=0)
    else:
        data = proximo_dia_util((now + timedelta(days=1)).date())
        due = datetime.combine(data, datetime.min.time()).replace(hour=12, minute=0)
    # retorna em ISO (bom para APIs)
    return due.isoformat()

items = []

for item in _input.all():
    raw_text = item.json.get("output", "")

    if not raw_text:
        items.append({"ok": False, "error": "Campo 'output' vazio", "raw": item.json})
        continue

    # Remove cercas ``` e a palavra json, e tamb√©m remove um "json" solto no in√≠cio
    cleaned = re.sub(r"```(?:json)?|```", "", raw_text, flags=re.IGNORECASE).strip()
    cleaned = re.sub(r"^\s*json\s*", "", cleaned, flags=re.IGNORECASE).strip()

    try:
        parsed = json.loads(cleaned)

        # ‚úÖ S√≥ calcula prazo quando PODE criar (para n√£o "inventar" prazo quando est√° faltando)
        if bool(parsed.get("pode_criar")):
            if not parsed.get("prazo") or str(parsed.get("prazo")).strip() == "":
                now = datetime.now()  # ou datetime.now(TZ) se voc√™ for√ßar fuso
                parsed["prazo"] = calcular_prazo(now)

        items.append({
            "ok": True,
            "pode_criar": bool(parsed.get("pode_criar", False)),
            "dados": parsed
        })

    except Exception as e:
        items.append({"ok": False, "error": str(e), "raw_text": raw_text})

return items
