  return String(str)
    .normalize("NFD").replace(/[\u0300-\u036f]/g, "") // remove acentos
    .replace(/[\/\\:\*\?"<>\|\r\n]+/g, "_")           // caracteres proibidos
    .replace(/\s+/g, " ")                            // normaliza espaços
    .trim()
    .replace(/\s/g, "_");                            // troca espaço por _
