// 3) Ajusta o date_time pra não ter ":" (se vier no formato com hora)
function sanitizeDateTime(dt) {
  // se vier vazio ou não-string
  if (!dt || typeof dt !== "string") return "sem_date_time";

  // Ex.: "2025-12-31T12:00:00.000Z" -> "2025-12-31T12_00_00.000Z"
  // Ex.: "2025-12-31 12:00:00"      -> "2025-12-31_12_00_00"
  return dt
    .replace(/:/g, "_")
    .replace(/\s+/g, "_");
}

// 4) Descobre extensão a partir do mimeType do binário (mantém jpg/png/pdf)
function inferExt(bin) {
  const mime = (bin?.mimeType || "").toLowerCase();
  if (mime.includes("jpeg")) return "jpg";
  if (mime.includes("png")) return "png";
  if (mime.includes("pdf")) return "pdf";

  // fallback: tenta usar extensão já existente
  const fromFileName = (bin?.fileName || "").split(".").pop();
  if (fromFileName && fromFileName.length <= 5) return fromFileName.toLowerCase();

  const fromFileExt = (bin?.fileExtension || "");
  if (fromFileExt) return String(fromFileExt).toLowerCase();

  return "bin";
}

