const pushName =
  $("Webhook").first().json?.body?.data?.pushName ?? "sem_pushName";

const dateTime =
  $("Webhook").first().json?.body?.date_time ?? "sem_date_time";

// 2) Sanitiza pra virar nome de arquivo seguro (Windows/Bitrix)
function sanitizeFilename(str) {
  return String(str)
    .normalize("NFD").replace(/[\u0300-\u036f]/g, "") // remove acentos
    .replace(/[\/\\:\*\?"<>\|\r\n]+/g, "_")           // caracteres proibidos
    .replace(/\s+/g, " ")                            // normaliza espa√ßos
    .trim()
    .replace(/\s/g, "_");                            // troca espa√ßo por _
}

// 3) Ajusta o date_time pra n√£o ter ":" (se vier no formato com hora)
function sanitizeDateTime(dt) {
  // se vier vazio ou n√£o-string
  if (!dt || typeof dt !== "string") return "sem_date_time";

  // Ex.: "2025-12-31T12:00:00.000Z" -> "2025-12-31T12_00_00.000Z"
  // Ex.: "2025-12-31 12:00:00"      -> "2025-12-31_12_00_00"
  return dt
    .replace(/:/g, "_")
    .replace(/\s+/g, "_");
}

// 4) Descobre extens√£o a partir do mimeType do bin√°rio (mant√©m jpg/png/pdf)
function inferExt(bin) {
  const mime = (bin?.mimeType || "").toLowerCase();
  if (mime.includes("jpeg")) return "jpg";
  if (mime.includes("png")) return "png";
  if (mime.includes("pdf")) return "pdf";

  // fallback: tenta usar extens√£o j√° existente
  const fromFileName = (bin?.fileName || "").split(".").pop();
  if (fromFileName && fromFileName.length <= 5) return fromFileName.toLowerCase();

  const fromFileExt = (bin?.fileExtension || "");
  if (fromFileExt) return String(fromFileExt).toLowerCase();

  return "bin";
}

// 5) Nome final: remoteJid__pushName__date_time.ext
const base = `${remoteJid}__${pushName}__${sanitizeDateTime(dateTime)}`;
let safeBase = sanitizeFilename(base);

// opcional: limita tamanho para evitar nomes enormes
if (safeBase.length > 140) safeBase = safeBase.slice(0, 140);

return items.map(item => {
  if (!item.binary || !item.binary[BIN_FIELD]) return item;

  const bin = item.binary[BIN_FIELD];
  const ext = inferExt(bin);

  bin.fileName = `${safeBase}.${ext}`;
  bin.fileExtension = ext;

  item.binary[BIN_FIELD] = bin;
  return item;
});

